// Processar solicita√ß√£o de agendamento
const data = $input.first().json;

const clientName = data.name || 'Cliente';
const clientEmail = data.email || '';
const clientPhone = data.phone || '';
const serviceType = data.serviceType || 'consultoria';
const preferredDate = data.preferredDate || '';
const preferredTime = data.preferredTime || '';
const message = data.message || '';
const duration = data.duration || 60; // minutos

// Mapear tipos de servi√ßo
const serviceConfig = {
  'consultoria': {
    duration: 60,
