    description: 'Consultoria especializada em automa√ß√£o empresarial',
    location: 'Online - Google Meet'
  },
  'demonstracao': {
    duration: 30,
    title: 'Demonstra√ß√£o da Plataforma',
    description: 'Demonstra√ß√£o das funcionalidades da KRYONIX',
    location: 'Online - Google Meet'
  },
