  },
  'onboarding': {
    duration: 90,
    title: 'Onboarding Completo',
    description: 'Configura√ß√£o inicial e treinamento',
    location: 'Online - Google Meet'
  }
};

const service = serviceConfig[serviceType] || serviceConfig['consultoria'];

// Gerar hor√°rios dispon√≠veis se n√£o especificado
const generateAvailableSlots = () => {
  const slots = [];
  const today = new Date();

  // Pr√≥ximos 7 dias √∫teis
  for (let i = 1; i <= 7; i++) {
    const date = new Date(today);
    date.setDate(today.getDate() + i);

    // Skip weekends
    if (date.getDay() === 0 || date.getDay() === 6) continue;

    // Hor√°rios: 9h √†s 18h
    for (let hour = 9; hour <= 17; hour++) {
      slots.push({
        date: date.toISOString().split('T')[0],
        time: `${hour.toString().padStart(2, '0')}:00`,
        datetime: new Date(date.getFullYear(), date.getMonth(), date.getDate(), hour, 0)
      });
    }
  }

  return slots;
};

const availableSlots = generateAvailableSlots();

return {
  clientName,
  clientEmail,
  clientPhone,
  serviceType,
  service,
  preferredDate,
  preferredTime,
  message,
  availableSlots: availableSlots.slice(0, 20), // Primeiros 20 slots
  requestId: `REQ_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
  createdAt: new Date().toISOString()
};

// Criar evento no Google Calendar
const requestData = $('Process Scheduling Request').first().json;
const preferredDateTime = new Date(`${requestData.preferredDate}T${requestData.preferredTime}:00`);
const endDateTime = new Date(preferredDateTime.getTime() + (requestData.service.duration * 60 * 1000));

// Gerar link do Google Meet
const meetLink = `https://meet.google.com/new`;

const eventData = {
  summary: `${requestData.service.title} - ${requestData.clientName}`,
  description: `${requestData.service.description}\n\nCliente: ${requestData.clientName}\nTelefone: ${requestData.clientPhone}\nEmail: ${requestData.clientEmail}\n\nMensagem: ${requestData.message}\n\nLink da reuni√£o: ${meetLink}`,
  start: {
    dateTime: preferredDateTime.toISOString(),
    timeZone: 'America/Sao_Paulo'
  },
  end: {
    dateTime: endDateTime.toISOString(),
    timeZone: 'America/Sao_Paulo'
  },
  attendees: [
    {
      email: requestData.clientEmail,
      displayName: requestData.clientName
    },
    {
      email: 'contato@kryonix.com',
      displayName: 'Equipe KRYONIX'
    }
  ],
  conferenceData: {
    createRequest: {
      requestId: requestData.requestId,
      conferenceSolutionKey: {
        type: 'hangoutsMeet'
      }
    }
  },
  reminders: {
    useDefault: false,
    overrides: [
      {
        method: 'email',
        minutes: 60
      },
      {
        method: 'popup',
        minutes: 15
      }
    ]
  }
};

return {
  eventData,
  clientData: requestData,
  meetLink
};

